def open_file_manager():
    file_path = filedialog.askopenfilename(title="Select a File")
    if file_path:
        messagebox.showinfo("File Selected", f"File Path: {file_path}")
        while True:
            ip_address = simpledialog.askstring("Input IP Address of Receiver", "Please enter a valid IP address:")
            if ip_address and validate_ip(ip_address):
                messagebox.showinfo("Connection in progress", f"Sending file to: {ip_address}")
                
                # Run Client.py and capture output
                result = subprocess.run(["python", "C:\\Users\\jeric\\OneDrive\\Desktop\\OPSYS APP\\build\\Client.py", ip_address, file_path], capture_output=True, text=True)

                # Check the result and show appropriate message
                if "SUCCESS" in result.stdout:
                    messagebox.showinfo("Success", "File sent successfully.")
                else:
                    messagebox.showerror("Failure", "File transfer failed.")
                break
            else:
                messagebox.showwarning("Invalid IP", "The IP address entered is invalid. Please try again.")

def validate_ip(ip):
    # Regular expression to validate IPv4 address format
    pattern = re.compile(r"^(?:[0-9]{1,3}\.){3}[0-9]{1,3}$")
    return bool(pattern.match(ip)) and all(0 <= int(num) <= 255 for num in ip.split("."))


